//패키지 이름은 kakaoGame입니다.
package kakaoGame;


//Apeach 클래스는 Character 클래스를 상속 받습니다.
public class Apeach extends Character {
	
	
	//디폴트 생성자를 정의합니다
		Apeach(){
			
			hp=40;
			energy=50;
			System.out.println("어피치가 생성되었습니다");
			printInfo();
			
		}

	@Override
	public void eat() {
		// TODO Auto-generated method stub
		energy+=15;
		
	}

	@Override
	public void sleep() {
		// TODO Auto-generated method stub
		energy+=10;
	}

	@Override
	public boolean play() {
		// TODO Auto-generated method stub
		energy-=30;
		hp+=15;
		return checkEnergy();
	}

	@Override
	public boolean train() {
		// TODO Auto-generated method stub
		energy-=20;
		hp+=30;
		levelUp();
		return checkEnergy();
	}
	@Override
	public void levelUp() {
		if(35<=hp) {
		level++;
		hp-=50;
		}
		// TODO Auto-generated method stub
		
	}

}
