//패키지 이름이 classTest입니다.
package classTest;

//실행 클래스의 이름이 Student입니다.

//Student 클래스를 정의합니다.
class Student{
	
	//필드(멤버변수)
	int studentID; //학번
	String studentName; //이름
	String major; //전공	
	int grade; //학년
}


public class StudentTest {
	// main()메서드가 생성되었습니다.
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//클래스형 변수(객체)이름 =new 생성자;
		
		Student studentA=new Student(); //Student클래스를 생성합니다.
		
		//Student() 클래스를 선택하고, 마우스 오른쪽 버튼을 클릭합니다.
		//F3키를 누릅니다.(또는 open declaration을 클릭합니다.)
		//classTest 패키지 안의 정의된 Student클래스로 이동합니다.
		
		//인스턴스를 여러개 만들어 보겠습니다.
		Student studentB=new Student();
		Student studentC=new Student();
		
		//참조 변수값 출력하기
		//studentA를 참조변수, 주소값을 참조값이라고 합니다.
		System.out.println(studentA);
		System.out.println(studentB);
		System.out.println(studentC);
	}

}
